import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useAuth } from "@/lib/auth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { type ChatMessage, type Project, type McpServer } from "@shared/schema";
import { 
  Mic, 
  Paperclip, 
  Send, 
  User, 
  Bot,
  Rocket,
  CheckCircle,
  Clock,
  Loader2,
  Monitor,
  MessageSquare
} from "lucide-react";

export default function ChatDevelopment() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const [chatInput, setChatInput] = useState("");
  const [appName, setAppName] = useState("");
  const [prompt, setPrompt] = useState("");
  const [selectedLLM, setSelectedLLM] = useState("llama");
  const [selectedServers, setSelectedServers] = useState<string[]>(["database", "api"]);
  const [buildProgress, setBuildProgress] = useState<"idle" | "building" | "completed">("idle");
  const [selectedProject, setSelectedProject] = useState<string | null>(null);
  const [appChatInput, setAppChatInput] = useState("");
  const [appChatMessages, setAppChatMessages] = useState<{id: string, sender: string, message: string}[]>([]);

  // Fetch data
  const { data: messages = [] } = useQuery<ChatMessage[]>({
    queryKey: ["/api/chat/messages"],
    queryFn: async () => {
      const response = await fetch(`/api/chat/messages?userId=${user?.id}`);
      return response.json();
    },
    enabled: !!user?.id,
    refetchInterval: 1000, // Poll for new messages
  });

  const { data: projects = [] } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
    queryFn: async () => {
      const response = await fetch(`/api/projects?userId=${user?.id}`);
      return response.json();
    },
    enabled: !!user?.id,
  });

  const { data: servers = [] } = useQuery<McpServer[]>({
    queryKey: ["/api/mcp-servers"],
    queryFn: async () => {
      const response = await fetch(`/api/mcp-servers?userId=${user?.id}`);
      return response.json();
    },
    enabled: !!user?.id,
  });

  // LLM options
  const llmOptions = [
    { value: "claude", label: "Claude 3.7" },
    { value: "gemini", label: "Gemini Pro" },
    { value: "llama", label: "LLaMA 3.3" },
    { value: "gpt4", label: "GPT-4" },
  ];

  // Mutations
  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      return apiRequest("POST", "/api/chat/messages", {
        userId: user?.id,
        sender: "user",
        message,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/chat/messages"] });
    },
  });

  const createProjectMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/projects", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/projects"] });
      toast({
        title: "App Generated!",
        description: "Your application has been created successfully.",
      });
      // Reset form
      setAppName("");
      setPrompt("");
      setBuildProgress("completed");
      setTimeout(() => setBuildProgress("idle"), 3000);
    },
  });

  // Auto-scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = async () => {
    if (!chatInput.trim()) return;

    try {
      await sendMessageMutation.mutateAsync(chatInput);
      setChatInput("");
    } catch (error) {
      toast({
        title: "Failed to send message",
        description: "Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleTransferPrompt = () => {
    const lastAIMessage = messages
      .filter(m => m.sender === "ai")
      .pop();

    if (lastAIMessage) {
      setPrompt(lastAIMessage.message);
      toast({
        title: "Prompt Transferred",
        description: "AI response has been transferred to the prompt area.",
      });
    }
  };

  const handleSaveApp = async () => {
    if (!appName.trim() || !prompt.trim() || selectedServers.length === 0) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields.",
        variant: "destructive",
      });
      return;
    }

    setBuildProgress("building");

    try {
      await createProjectMutation.mutateAsync({
        userId: user?.id,
        name: appName,
        prompt: prompt,
        llm: selectedLLM,
        mcpServers: selectedServers,
        status: "development",
        files: [
          { name: "Knowledge Article 1", size: "12.4kb", type: "markdown" },
          { name: "Knowledge Article 2", size: "3.2kb", type: "markdown" },
          { name: "Knowledge Article 3", size: "2.1kb", type: "markdown" },
        ],
      });
    } catch (error) {
      setBuildProgress("idle");
      toast({
        title: "Failed to create app",
        description: "Please try again.",
        variant: "destructive",
      });
    }
  };

  const toggleServer = (serverId: string) => {
    setSelectedServers(prev => 
      prev.includes(serverId) 
        ? prev.filter(id => id !== serverId)
        : [...prev, serverId]
    );
  };

  const getAppTypeResponses = (appType: string, question: string): string => {
    const responses: Record<string, Record<string, string>> = {
      "Restaurant App": {
        "what is today's menu": "Today's menu includes our signature dishes:\n\nðŸ Pasta Primavera - $18\nðŸ¥— Caesar Salad - $12\nðŸ– Grilled Ribeye - $32\nðŸ¤ Garlic Shrimp - $24\nðŸ° Tiramisu - $8\n\nWould you like to place an order or make a reservation?",
        "menu": "Here's our full menu:\n\n**APPETIZERS**\nâ€¢ Bruschetta - $9\nâ€¢ Calamari - $12\n\n**MAINS**\nâ€¢ Pasta Primavera - $18\nâ€¢ Grilled Salmon - $28\nâ€¢ Ribeye Steak - $32\n\n**DESSERTS**\nâ€¢ Tiramisu - $8\nâ€¢ Gelato - $6",
        "reservation": "I'd be happy to help with reservations! We have availability today from 6-9 PM. How many guests and what time would you prefer?",
        "default": "Welcome to our restaurant! I can help you with our menu, reservations, or placing an order. What would you like to know?"
      },
      "E-commerce Store": {
        "products": "Here are our featured products:\n\nðŸ“± iPhone 15 Pro - $999\nðŸ’» MacBook Air - $1299\nâŒš Apple Watch - $399\nðŸŽ§ AirPods Pro - $249\n\nAll items come with free shipping and 30-day returns!",
        "order": "To place an order, simply add items to your cart and proceed to checkout. We accept all major credit cards and PayPal.",
        "shipping": "We offer free shipping on orders over $50. Standard delivery is 3-5 business days, or choose express delivery for next-day delivery.",
        "default": "Welcome to our store! Browse our products, check your orders, or ask me about shipping and returns. How can I help you today?"
      },
      "Blog Platform": {
        "posts": "Here are our latest blog posts:\n\nâœï¸ 'Getting Started with React' - Dec 18\nðŸš€ 'Web Performance Tips' - Dec 17\nðŸ’¡ 'Design Trends 2025' - Dec 16\nðŸ”§ 'JavaScript Best Practices' - Dec 15\n\nWhich topic interests you most?",
        "write": "Ready to create a new post? I can help you with:\nâ€¢ Post ideas and topics\nâ€¢ SEO optimization\nâ€¢ Content structure\nâ€¢ Publishing schedule\n\nWhat would you like to write about?",
        "default": "Welcome to our blog platform! I can help you discover content, create new posts, or manage your publications. What are you looking for?"
      },
      "Fitness Tracker": {
        "workout": "Here's today's recommended workout:\n\nðŸ’ª **Upper Body Strength**\nâ€¢ Push-ups: 3 sets of 12\nâ€¢ Dumbbell rows: 3 sets of 10\nâ€¢ Shoulder press: 3 sets of 8\nâ€¢ Plank: 3 sets of 30s\n\nEstimated time: 45 minutes. Ready to start?",
        "progress": "Your fitness progress this week:\n\nðŸ“Š Workouts completed: 4/5\nðŸ”¥ Calories burned: 1,240\nâ±ï¸ Total workout time: 3h 20m\nðŸ’ª Strength improved by 5%\n\nKeep up the great work!",
        "default": "Hi there! I'm your fitness assistant. I can help you track workouts, monitor progress, plan exercises, and stay motivated. What's your fitness goal today?"
      },
      "Task Manager": {
        "tasks": "Here are your current tasks:\n\nâœ… **Completed:**\nâ€¢ Review project proposal\nâ€¢ Team standup meeting\n\nðŸ“‹ **In Progress:**\nâ€¢ Update documentation\nâ€¢ Code review for feature X\n\nâ° **Pending:**\nâ€¢ Schedule client call\nâ€¢ Prepare presentation",
        "project": "Your active projects:\n\nðŸš€ **Website Redesign** (75% complete)\nðŸ‘¥ **Team Onboarding** (40% complete)\nðŸ“± **Mobile App** (90% complete)\n\nWhich project would you like to focus on?",
        "default": "Welcome to your task manager! I can help you organize tasks, track project progress, collaborate with your team, and meet deadlines. What do you need to accomplish today?"
      }
    };

    const appResponses = responses[appType] || {};
    const questionLower = question.toLowerCase();

    for (const [key, response] of Object.entries(appResponses)) {
      if (questionLower.includes(key)) {
        return response;
      }
    }

    return appResponses.default || "I'm here to help! What would you like to know about this app?";
  };

  const handleAppChatSend = () => {
    if (!appChatInput.trim() || !selectedProject) return;

    const selectedProjectData = projects.find(p => p.id === selectedProject);
    if (!selectedProjectData) return;

    const userMessage = {
      id: `msg-${Date.now()}-user`,
      sender: "user",
      message: appChatInput
    };

    const aiResponse = {
      id: `msg-${Date.now()}-ai`,
      sender: "ai", 
      message: getAppTypeResponses(selectedProjectData.name, appChatInput)
    };

    setAppChatMessages(prev => [...prev, userMessage, aiResponse]);
    setAppChatInput("");
  };

  return (
    <div className="flex flex-col h-[calc(100vh-8rem)] space-y-4">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 flex-1 min-h-0">
        {/* Main Chat Area */}
        <div className="lg:col-span-2 flex flex-col space-y-4 min-h-0">
          {/* Chat Messages */}
          <Card className="flex-1 shadow-sm border border-gray-100 min-h-0">
            <CardContent className="p-6 h-full overflow-y-auto">
              <div className="space-y-4">
                {messages.length === 0 ? (
                  <div className="text-center py-12">
                    <Bot size={48} className="mx-auto text-gray-400 mb-4" />
                    <p className="text-gray-500">Start a conversation to develop your app</p>
                  </div>
                ) : (
                  messages.map((message) => (
                    <div key={message.id} className="flex items-start space-x-3">
                      <div className={`rounded-full p-2 ${
                        message.sender === "user" 
                          ? "bg-gray-200" 
                          : "bg-blue-500"
                      }`}>
                        {message.sender === "user" ? (
                          <User size={16} className="text-gray-600" />
                        ) : (
                          <Bot size={16} className="text-white" />
                        )}
                      </div>
                      <div className={`rounded-lg p-3 max-w-2xl ${
                        message.sender === "user" 
                          ? "bg-gray-100" 
                          : "bg-blue-50"
                      }`}>
                        <p className="text-gray-800 whitespace-pre-wrap">
                          {message.message}
                        </p>
                      </div>
                    </div>
                  ))
                )}
                <div ref={messagesEndRef} />
              </div>
            </CardContent>
          </Card>

          {/* Chat Input */}
          <Card className="shadow-sm border border-gray-100">
            <CardContent className="p-4">
              <div className="flex items-center space-x-3">
                <Input
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                  placeholder="Ask AI to generate a persona prompt for your agent"
                  className="flex-1"
                  onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
                  data-testid="input-chat-message"
                />
                <Button
                  onClick={handleTransferPrompt}
                  className="bg-orange-500 hover:bg-orange-600 text-white whitespace-nowrap"
                  data-testid="button-transfer-prompt"
                >
                  Transfer left ready prompt
                </Button>
                <Button variant="outline" size="icon" data-testid="button-voice-input">
                  <Mic size={16} />
                </Button>
                <Button variant="outline" size="icon" data-testid="button-attach-file">
                  <Paperclip size={16} />
                </Button>
                <Button 
                  onClick={handleSendMessage}
                  disabled={sendMessageMutation.isPending}
                  data-testid="button-send-message"
                >
                  <Send size={16} />
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Right Panel - Live Preview */}
        <div className="lg:col-span-1 flex flex-col space-y-4 min-h-0">
          <Card className="shadow-sm border border-gray-100">
            <CardContent className="p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Live Preview</h3>

              {/* Project Selection */}
              <div className="mb-6">
                <Label className="text-sm font-medium text-gray-700 mb-2 block">Select App to Preview</Label>
                <Select value={selectedProject || ""} onValueChange={setSelectedProject}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Choose an app to preview" />
                  </SelectTrigger>
                  <SelectContent>
                    {projects.filter(p => p.status === "completed").map((project) => (
                      <SelectItem key={project.id} value={project.id}>
                        {project.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {selectedProject ? (
                <div className="space-y-4">
                  {/* App-specific Chat */}
                  <div className="border border-gray-200 rounded-lg">
                    <div className="bg-gray-50 px-3 py-2 border-b border-gray-200">
                      <div className="flex items-center space-x-2">
                        <MessageSquare size={16} className="text-gray-600" />
                        <span className="text-sm font-medium text-gray-700">Chat with your app</span>
                      </div>
                    </div>

                    {/* Chat Messages */}
                    <div className="h-40 overflow-y-auto p-3 space-y-2">
                      {appChatMessages.length === 0 ? (
                        <div className="text-center py-4">
                          <p className="text-xs text-gray-500">Start chatting with your {projects.find(p => p.id === selectedProject)?.name}</p>
                        </div>
                      ) : (
                        appChatMessages.map((msg) => (
                          <div key={msg.id} className={`text-xs p-2 rounded max-w-[80%] ${
                            msg.sender === "user" 
                              ? "bg-blue-100 text-blue-900 ml-auto" 
                              : "bg-gray-100 text-gray-900"
                          }`}>
                            <div className="whitespace-pre-wrap">{msg.message}</div>
                          </div>
                        ))
                      )}
                    </div>

                    {/* Chat Input */}
                    <div className="border-t border-gray-200 p-3">
                      <div className="flex space-x-2">
                        <Input
                          value={appChatInput}
                          onChange={(e) => setAppChatInput(e.target.value)}
                          placeholder={`Ask your ${projects.find(p => p.id === selectedProject)?.name.toLowerCase()}...`}
                          className="text-xs"
                          onKeyPress={(e) => e.key === "Enter" && handleAppChatSend()}
                          data-testid="input-app-chat"
                        />
                        <Button
                          onClick={handleAppChatSend}
                          size="sm"
                          data-testid="button-send-app-chat"
                        >
                          <Send size={12} />
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8">
                  <Monitor size={32} className="mx-auto text-gray-400 mb-2" />
                  <p className="text-sm text-gray-500">Select an app to start chatting</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>

      {/* App Configuration - Fixed at bottom */}
      <Card className="shadow-sm border border-gray-100">
        <CardContent className="p-6">
          <div className="grid grid-cols-1 lg:grid-cols-12 gap-4 items-end">
            {/* App Name */}
            <div className="lg:col-span-2">
              <Label htmlFor="app-name">App Name</Label>
              <Input
                id="app-name"
                value={appName}
                onChange={(e) => setAppName(e.target.value)}
                placeholder="Analytics Dashboard"
                data-testid="input-app-name"
              />
            </div>

            {/* Prompt */}
            <div className="lg:col-span-4">
              <Label htmlFor="main-prompt">Prompt</Label>
              <Textarea
                id="main-prompt"
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                rows={2}
                placeholder="Create a comprehensive user analytics dashboard..."
                data-testid="textarea-main-prompt"
              />
            </div>

            {/* LLM Selection */}
            <div className="lg:col-span-2">
              <Label className="text-sm font-medium">LLM</Label>
              <Select value={selectedLLM} onValueChange={setSelectedLLM}>
                <SelectTrigger className="w-full">
                  <SelectValue placeholder="Select LLM" />
                </SelectTrigger>
                <SelectContent>
                  {llmOptions.map((llm) => (
                    <SelectItem key={llm.value} value={llm.value}>
                      {llm.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* MCP Servers */}
            <div className="lg:col-span-2">
              <Label className="text-sm font-medium">MCP Servers</Label>
              <Select onValueChange={(value) => toggleServer(value)}>
                <SelectTrigger className="w-full">
                  <SelectValue placeholder={`${selectedServers.length} selected`} />
                </SelectTrigger>
                <SelectContent>
                  {servers.map((server) => (
                    <SelectItem key={server.id} value={server.id}>
                      <div className="flex items-center justify-between w-full">
                        <span>{server.name}</span>
                        {selectedServers.includes(server.id) && (
                          <CheckCircle size={14} className="text-green-600" />
                        )}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {selectedServers.length > 0 && (
                <div className="flex flex-wrap gap-1 mt-1">
                  {selectedServers.map((serverId) => {
                    const server = servers.find(s => s.id === serverId);
                    return server ? (
                      <Badge key={serverId} variant="secondary" className="text-xs">
                        {server.name}
                      </Badge>
                    ) : null;
                  })}
                </div>
              )}
            </div>

            {/* Save Button */}
            <div className="lg:col-span-2">
              <Button
                onClick={handleSaveApp}
                disabled={createProjectMutation.isPending || buildProgress === "building"}
                className="w-full"
                data-testid="button-save-app"
              >
                {buildProgress === "building" ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Building...
                  </>
                ) : (
                  "Save App"
                )}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/lib/auth";
import { useLocation } from "wouter";
import { type Project } from "@shared/schema";
import { 
  Smartphone, 
  Users, 
  Network, 
  Store,
  Eye,
  MessageCircle,
  DollarSign,
  TrendingUp
} from "lucide-react";

export default function Dashboard() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();

  const { data: projects = [], isLoading } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
    queryFn: async () => {
      const response = await fetch(`/api/projects?userId=${user?.id}`);
      return response.json();
    },
    enabled: !!user?.id,
  });

  const { data: servers = [] } = useQuery({
    queryKey: ["/api/mcp-servers"],
    queryFn: async () => {
      const response = await fetch(`/api/mcp-servers?userId=${user?.id}`);
      return response.json();
    },
    enabled: !!user?.id,
  });

  const { data: marketplaceApps = [] } = useQuery({
    queryKey: ["/api/marketplace"],
    queryFn: async () => {
      const response = await fetch("/api/marketplace");
      return response.json();
    },
  });

  const totalRevenue = projects
    .filter(p => p.status === "completed")
    .reduce((sum, p: any) => sum + (p.revenue || 0), 0);

  const metrics = {
    totalApps: projects.length,
    activeProjects: projects.filter(p => p.status !== "completed").length,
    mcpConnections: servers.filter((s: any) => s.status === "connected").length,
    revenue: totalRevenue,
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "completed":
        return <Badge className="status-completed">âœ… Completed</Badge>;
      case "testing":
        return <Badge className="status-testing">ðŸ”„ Testing</Badge>;
      case "development":
        return <Badge className="status-development">ðŸ”„ Development</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-20 bg-gray-200 rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Metrics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="shadow-sm border border-gray-100">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Generated Apps</p>
                <p className="text-3xl font-bold text-gray-900" data-testid="metric-total-apps">
                  {metrics.totalApps}
                </p>
              </div>
              <div className="bg-blue-100 p-3 rounded-full">
                <Smartphone className="text-blue-600" size={24} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="shadow-sm border border-gray-100">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Active Projects</p>
                <p className="text-3xl font-bold text-gray-900" data-testid="metric-active-projects">
                  {metrics.activeProjects}
                </p>
              </div>
              <div className="bg-green-100 p-3 rounded-full">
                <Users className="text-green-600" size={24} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="shadow-sm border border-gray-100">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">MCP Connections</p>
                <p className="text-3xl font-bold text-gray-900" data-testid="metric-mcp-connections">
                  {metrics.mcpConnections}
                </p>
              </div>
              <div className="bg-purple-100 p-3 rounded-full">
                <Network className="text-purple-600" size={24} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card 
          className="shadow-sm border border-gray-100 hover:shadow-md transition-shadow cursor-pointer" 
          onClick={() => setLocation('/analytics')}
          data-testid="card-revenue-metric"
        >
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Revenue Earned</p>
                <p className="text-3xl font-bold text-gray-900" data-testid="metric-revenue">
                  ${(metrics.revenue / 100).toFixed(2)}
                </p>
                <p className="text-sm text-green-600 flex items-center mt-1">
                  <TrendingUp size={14} className="mr-1" />
                  +12.5% this month
                </p>
              </div>
              <div className="bg-green-100 p-3 rounded-full">
                <DollarSign className="text-green-600" size={24} />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Projects */}
      <Card className="shadow-sm border border-gray-100">
        <CardContent className="p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-6">Recent Projects</h2>

          {projects.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-500">No projects yet. Start building your first app!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {projects.slice(0, 3).map((project) => (
                <Card key={project.id} className="border border-gray-200 hover:shadow-md transition-shadow">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between mb-3">
                      <h3 className="font-semibold text-gray-900" data-testid={`project-name-${project.id}`}>
                        {project.name}
                      </h3>
                      {getStatusBadge(project.status)}
                    </div>

                    <div className="space-y-2 mb-4">
                      <p className="text-sm text-gray-600">
                        Generated: {project.createdAt ? new Date(project.createdAt).toLocaleDateString() : 'Unknown'}
                      </p>
                      <p className="text-sm text-gray-600">
                        LLM: {project.llm.charAt(0).toUpperCase() + project.llm.slice(1)}
                      </p>
                      {project.status === "completed" && (project as any).revenue && (
                        <div className="flex items-center justify-between">
                          <p className="text-sm text-green-600 font-medium">
                            Revenue: ${((project as any).revenue / 100).toFixed(2)}
                          </p>
                          <span className="text-xs text-green-600 bg-green-50 px-2 py-1 rounded-full">
                            +{(project as any).revenueGrowth || 15}%
                          </span>
                        </div>
                      )}
                      <p className="text-sm text-gray-600">
                        Files: {project.files?.length || 0} files
                      </p>
                    </div>

                    <div className="flex space-x-2">
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => setLocation(`/projects?view=${project.id}`)}
                        data-testid={`button-view-${project.id}`}
                      >
                        <Eye size={16} className="mr-1" />
                        View
                      </Button>
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => setLocation(`/chat-development`)}
                        data-testid={`button-chat-${project.id}`}
                      >
                        <MessageCircle size={16} className="mr-1" />
                        Chat
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { Box, Mail, Lock, Chrome, Github } from "lucide-react";
import { useAuth } from "@/lib/auth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function LoginPage() {
  const [email, setEmail] = useState("demo@mcpbuilder.com");
  const [password, setPassword] = useState("demo123");
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await apiRequest("POST", "/api/auth/login", { email, password });
      const data = await response.json();

      login(data.user);

      toast({
        title: "Welcome back!",
        description: "You have successfully logged in.",
      });
    } catch (error) {
      toast({
        title: "Login failed",
        description: "Invalid credentials. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen gradient-bg flex items-center justify-center p-4">
      <Card className="w-full max-w-md shadow-xl">
        <CardContent className="p-8">
          <div className="text-center mb-8">
            <div className="gradient-brand text-white w-16 h-16 rounded-xl flex items-center justify-center mx-auto mb-4">
              <Box size={32} />
            </div>
            <h1 className="text-2xl font-bold text-gray-900">MCP Builder</h1>
            <p className="text-gray-600 mt-2">Build AI apps with conversation</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <Label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">
                Email
              </Label>
              <div className="relative">
                <Mail className="absolute left-3 top-3 h-5 w-5 text-gray-400" />
                <Input
                  id="email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="pl-10"
                  placeholder="demo@mcpbuilder.com"
                  data-testid="input-email"
                  required
                />
              </div>
            </div>

            <div>
              <Label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-2">
                Password
              </Label>
              <div className="relative">
                <Lock className="absolute left-3 top-3 h-5 w-5 text-gray-400" />
                <Input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="pl-10"
                  placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                  data-testid="input-password"
                  required
                />
              </div>
            </div>

            <Button 
              type="submit" 
              className="w-full gradient-brand py-3 font-medium hover:opacity-90 transition-opacity"
              disabled={isLoading}
              data-testid="button-login"
            >
              {isLoading ? "Signing In..." : "Sign In"}
            </Button>

            <div className="text-center">
              <p className="text-gray-500 mb-4">Or continue with</p>
              <div className="flex space-x-4">
                <Button 
                  type="button" 
                  variant="outline" 
                  className="flex-1"
                  data-testid="button-google-login"
                >
                  <Chrome className="mr-2 h-4 w-4 text-red-500" />
                  Google
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  className="flex-1"
                  data-testid="button-github-login"
                >
                  <Github className="mr-2 h-4 w-4" />
                  GitHub
                </Button>
              </div>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/marketplace.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { type MarketplaceApp } from "@shared/schema";
import { Search, Download, Eye, Star, Store } from "lucide-react";

const categories = [
  { id: "all", name: "All" },
  { id: "business", name: "Business" },
  { id: "content", name: "Content" },
  { id: "service", name: "Service" },
  { id: "custom", name: "Custom" },
];

export default function Marketplace() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");

  const { data: apps = [], isLoading } = useQuery<MarketplaceApp[]>({
    queryKey: ["/api/marketplace"],
    queryFn: async () => {
      const response = await fetch("/api/marketplace");
      return response.json();
    },
  });

  // Add some demo marketplace apps for display
  const demoApps = [
    {
      id: "demo-1",
      name: "E-commerce Store",
      description: "Complete e-commerce solution",
      price: 4900,
      rating: "4.8",
      downloads: 1200,
      category: "business",
      icon: "âš¡",
    },
    {
      id: "demo-2",
      name: "Blog Platform",
      description: "Modern blogging platform",
      price: 2900,
      rating: "4.6",
      downloads: 890,
      category: "content",
      icon: "ðŸ“Š",
    },
    {
      id: "demo-3",
      name: "Booking System",
      description: "Appointment booking system",
      price: 3900,
      rating: "4.9",
      downloads: 2100,
      category: "service",
      icon: "ðŸ’¬",
    },
  ];

  const allApps = [...demoApps, ...apps];

  const filteredApps = allApps.filter((app) => {
    const matchesSearch = app.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         app.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "all" || app.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const formatPrice = (priceInCents: number) => {
    return `$${(priceInCents / 100).toFixed(0)}`;
  };

  const formatDownloads = (downloads: number | null) => {
    if (!downloads) return "0";
    if (downloads >= 1000) {
      return `${(downloads / 1000).toFixed(1)}K`;
    }
    return downloads.toString();
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="space-y-4">
                  <div className="h-12 bg-gray-200 rounded"></div>
                  <div className="h-6 bg-gray-200 rounded"></div>
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <h2 className="text-2xl font-bold text-gray-900">Marketplace</h2>

        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search size={16} className="absolute left-3 top-3 text-gray-400" />
            <Input
              placeholder="Search apps..."
              className="pl-10 w-64"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              data-testid="input-search-apps"
            />
          </div>
        </div>
      </div>

      {/* Category Filters */}
      <div className="flex space-x-2 overflow-x-auto">
        {categories.map((category) => (
          <Button
            key={category.id}
            variant={selectedCategory === category.id ? "default" : "outline"}
            size="sm"
            onClick={() => setSelectedCategory(category.id)}
            className="whitespace-nowrap"
            data-testid={`filter-${category.id}`}
          >
            {category.name}
          </Button>
        ))}
      </div>

      {/* Apps Grid */}
      {filteredApps.length === 0 ? (
        <Card>
          <CardContent className="p-12 text-center">
            <Store size={48} className="mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-semibold text-gray-900 mb-2">No apps found</h3>
            <p className="text-gray-600">Try adjusting your search or category filter.</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredApps.map((app) => (
            <Card key={app.id} className="hover:shadow-md transition-shadow border border-gray-100">
              <CardContent className="p-6">
                <div className="text-center mb-4">
                  <div className="text-4xl mb-2">{app.icon}</div>
                  <h3 className="text-lg font-semibold text-gray-900" data-testid={`app-name-${app.id}`}>
                    {app.name}
                  </h3>
                  {app.description && (
                    <p className="text-sm text-gray-600 mt-1">{app.description}</p>
                  )}
                </div>

                <div className="space-y-3 mb-4">
                  <div className="flex items-center justify-between">
                    <span className="text-2xl font-bold text-gray-900">
                      {formatPrice(app.price)}
                    </span>
                    <div className="flex items-center space-x-1">
                      <Star size={16} className="text-yellow-500 fill-current" />
                      <span className="text-sm text-gray-600">{app.rating}</span>
                    </div>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-600">
                      {formatDownloads(app.downloads)} Downloads
                    </span>
                    <Badge variant="outline" className="capitalize">
                      {app.category}
                    </Badge>
                  </div>
                </div>

                <div className="flex space-x-2">
                  <Button 
                    className="flex-1" 
                    size="sm"
                    data-testid={`button-install-${app.id}`}
                  >
                    <Download size={16} className="mr-1" />
                    Install
                  </Button>
                  <Button 
                    variant="outline" 
                    className="flex-1" 
                    size="sm"
                    data-testid={`button-details-${app.id}`}
                  >
                    <Eye size={16} className="mr-1" />
                    Details
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

